import React, { useState } from 'react';
import './App.css';

const Visibility = () => {
  const [showLargeBox, setShowLargeBox] = useState(false);
  const [showStatusBox, setShowStatusBox] = useState(false);
  const [status, setStatus] = useState('');
  const [employees, setEmployees] = useState([1, 2, 3]);
  const [tasks, setTasks] = useState({
    1: ['Task 1', 'Task 2', 'Task 3'],
    2: ['Task 1', 'Task 2'],
    3: ['Task 1'],
  });

  const [taskColors, setTaskColors] = useState({}); // New state to store task colors

  const handleManagerClick = () => {
    setShowLargeBox((prev) => !prev);
    setStatus('Not Started');
  };

  const addEmployee = () => {
    const newId = employees.length > 0 ? Math.max(...employees) + 1 : 1;
    setEmployees((prev) => [...prev, newId]);
    setTasks((prev) => ({ ...prev, [newId]: [] }));
  };

  const deleteEmployee = (employeeId) => {
    setEmployees((prev) => prev.filter((emp) => emp !== employeeId));
    setTasks((prev) => {
      const newTasks = { ...prev };
      delete newTasks[employeeId];
      return newTasks;
    });
  };

  const deleteTask = (employeeId, taskIndex) => {
    setTasks((prev) => {
      const updatedTasks = { ...prev };
      updatedTasks[employeeId] = updatedTasks[employeeId].filter(
        (_, index) => index !== taskIndex
      );
      return updatedTasks;
    });
  };

  const addTask = (employeeId) => {
    const newTask = `Task ${tasks[employeeId].length + 1}`;
    setTasks((prev) => ({
      ...prev,
      [employeeId]: [...prev[employeeId], newTask],
    }));
  };

  const changeTaskStatus = (newStatus) => {
    let color = '';
    if (newStatus === 'Not Started') color = '#828282';
    if (newStatus === 'In Progress') color = 'orange';
    if (newStatus === 'Testing') color = 'yellow';
    if (newStatus === 'Done') color = 'green';

    // Update task colors based on status
    setTaskColors((prevColors) => {
      const updatedColors = { ...prevColors };
      Object.keys(updatedColors).forEach((key) => {
        updatedColors[key] = color;
      });
      return updatedColors;
    });

    setStatus(newStatus);
    setShowStatusBox(false);
    setShowLargeBox(true);
  };

  return (
    <div>
      <div className="taskbox">
        <button onClick={handleManagerClick}>Manager</button>
      </div>

      <div className="taskbox2">
        <button id="editbtn" onClick={addEmployee}>
          +
        </button>
      </div>

      <div className={showLargeBox ? '' : 'hidden'}>
        <div className="largebox">
          {employees.map((emp) => (
            <div className="empbox" key={emp}>
              <h3>Employee {emp}</h3>

              <ul>
                {tasks[emp].map((task, index) => (
                  <li key={index} style={{ backgroundColor: taskColors[task] }}>
                    <button>{task}</button>
                    <button onClick={() => deleteTask(emp, index)}>üóëÔ∏è</button>
                  </li>
                ))}
              </ul>
              <button onClick={() => addTask(emp)}>Add New Task</button>
              <button onClick={() => deleteEmployee(emp)}>Delete Employee</button>
            </div>
          ))}
        </div>
      </div>

      <div className={showStatusBox ? '' : 'hidden'}>
        <div className="statusbox">
          <ul>
            <li>
              <button onClick={() => changeTaskStatus('Not Started')}>
                Not Started
              </button>
            </li>
            <li>
              <button onClick={() => changeTaskStatus('In Progress')}>
                In Progress
              </button>
            </li>
            <li>
              <button onClick={() => changeTaskStatus('Testing')}>
                Testing
              </button>
            </li>
            <li>
              <button onClick={() => changeTaskStatus('Done')}>Done</button>
            </li>
          </ul>
          <p>Status: {status}</p>
          <button
            onClick={() => {
              setShowStatusBox(false);
              setShowLargeBox(true);
            }}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default Visibility;
