import React, { useState, useRef } from 'react';
import './App.css';

const Visibility = () => {
  const [showLargeBox, setShowLargeBox] = useState(false);
  const [showStatusBox, setShowStatusBox] = useState(false);
  const [status, setStatus] = useState('');
  const [employees, setEmployees] = useState([1, 2, 3]);
  const [tasks, setTasks] = useState({
    1: ['Task 1', 'Task 2', 'Task 3'],
    2: ['Task 1', 'Task 2'],
    3: ['Task 1'],
  });

  const [showInputBox, setShowInputBox] = useState(null);
  const [taskInput, setTaskInput] = useState('');
  const taskRefs = useRef({}); // Using useRef to store task references

  const handleManagerClick = () => {
    setShowLargeBox((prev) => !prev);
    setStatus('Not Started');
  };

  const handleTaskClick = (task) => {
    setShowStatusBox(true);
    setShowLargeBox(false);
  };

  const addEmployee = () => {
    const newId = employees.length > 0 ? Math.max(...employees) + 1 : 1;
    setEmployees((prev) => [...prev, newId]);
    setTasks((prev) => ({ ...prev, [newId]: [] }));
  };

  const confirmAddTask = (employeeId) => {
    if (!taskInput.trim()) return;
    setTasks((prev) => ({
      ...prev,
      [employeeId]: [...prev[employeeId], taskInput.trim()],
    }));
    setTaskInput('');
    setShowInputBox(null);
  };

  const deleteEmployee = (employeeId) => {
    setEmployees((prev) => prev.filter((emp) => emp !== employeeId));
    setTasks((prev) => {
      const newTasks = { ...prev };
      delete newTasks[employeeId];
      return newTasks;
    });
  };

  const deleteTask = (employeeId, taskIndex) => {
    setTasks((prev) => {
      const updatedTasks = { ...prev };
      updatedTasks[employeeId] = updatedTasks[employeeId].filter(
        (_, index) => index !== taskIndex
      );
      return updatedTasks;
    });
  };

  const changeTaskStatus = (newStatus, task) => {
    let backgroundColor = '';
    switch (newStatus) {
      case 'Not Started':
        backgroundColor = '#828282';
        break;
      case 'In Progress':
        backgroundColor = 'orange';
        break;
      case 'Testing':
        backgroundColor = 'yellow';
        break;
      case 'Done':
        backgroundColor = 'green';
        break;
      default:
        break;
    }

    // Apply background color to the clicked task
    if (taskRefs.current[task]) {
      taskRefs.current[task].style.backgroundColor = backgroundColor;
    }

    setStatus(newStatus);
    setShowStatusBox(false);
    setShowLargeBox(true);
  };

  return (
    <div>
      <div className="taskbox">
        <button onClick={handleManagerClick}>Manager</button>
      </div>

      <div className="taskbox2">
        <button id="editbtn" onClick={addEmployee}>
          +
        </button>
      </div>

      <div className={showLargeBox ? '' : 'hidden'}>
        <div className="largebox">
          {employees.map((emp) => (
            <div className="empbox" key={emp}>
              <h3>Employee {emp}</h3>

              <ul style={{ listStyleType: 'disc', paddingLeft: '20px' }}>
                {(tasks[emp] || []).map((task, index) => (
                  <li
                    key={index}
                    style={{ display: 'flex', justifyContent: 'space-between' }}
                  >
                    <button
                      onClick={() => handleTaskClick(task)}
                      ref={(node) => (taskRefs.current[task] = node)} // Store the reference to the task button
                    >
                      {task}
                    </button>
                    <button onClick={() => deleteTask(emp, index)}>üóëÔ∏è</button> {/* Delete task button */}
                  </li>
                ))}
              </ul>

              {showInputBox === emp ? (
                <div>
                  <input
                    type="text"
                    placeholder="Enter task name"
                    value={taskInput}
                    onChange={(e) => setTaskInput(e.target.value)}
                  />
                  <button onClick={() => confirmAddTask(emp)}>Add</button>
                  <button onClick={() => setShowInputBox(null)}>Cancel</button>
                </div>
              ) : (
                <div style={{ display: 'flex', gap: '8px' }}>
                  <button onClick={() => setShowInputBox(emp)}>Add New Task</button>
                  <button onClick={() => deleteEmployee(emp)}>üóëÔ∏è</button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      <div className={showStatusBox ? '' : 'hidden'}>
        <div className="statusbox">
          <ul>
            <li>
              <button
                onClick={() => changeTaskStatus('Not Started', status)}
              >
                Not Started
              </button>
            </li>
            <li>
              <button
                onClick={() => changeTaskStatus('In Progress', status)}
              >
                In Progress
              </button>
            </li>
            <li>
              <button
                onClick={() => changeTaskStatus('Testing', status)}
              >
                Testing
              </button>
            </li>
            <li>
              <button
                onClick={() => changeTaskStatus('Done', status)}
              >
                Done
              </button>
            </li>
          </ul>
          <p>Status: {status}</p>
          <button
            onClick={() => {
              setShowStatusBox(false);
              setShowLargeBox(true);
            }}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default Visibility;
