import React, { useState } from 'react';
import './styles.css'; 
const Visibility = () => {

  const [showLargeBox, setShowLargeBox] = useState(false);
  const [showTaskBox, setShowTaskBox] = useState(true);
  const [showStatusBox, setShowStatusBox] = useState(true);
  const [status, setStatus] = useState('');

  const handleManagerClick = () => {
    setShowLargeBox(true);             // Show large box
    setShowTaskBox(prev => !prev);     // Toggle task box
    setShowStatusBox(false);           // Hide status box
    setStatus('Not Started');          // Update status text
  };

  return (
    <div>
      <button onClick={handleManagerClick}>Manager</button>

      <div className={showLargeBox ? '' : 'hidden'}>
        <p>Large Box</p>
      </div>

      <div className={showTaskBox ? '' : 'hidden'}>
        <p>Task Box</p>
      </div>

      <div className={showStatusBox ? '' : 'hidden'}>
        <p>Status Box</p>
      </div>

      <p>Status: {status}</p>
    </div>


);
}
export default Visibility;
